const validStatus = ["pending", "snoozed", "open"];
function Filters(conversation, inbox){
  if(!conversation) return false;
  if(inbox){
    if(conversation.inbox_id != inbox)
      return false;
  }
  return validStatus.includes(conversation.status);
}


for (const item of $input.all()){
  if(item.json.payload){

      // applying filters
      const conversations = item.json.payload.filter((e) => Filters(e, item.json.inbox));      
      item.json.payload = conversations?.shift();
  } 
}

return $input.all();

function a2hex(str) {
  var arr = [];
  for (var i = 0, l = str.length; i < l; i ++) {
    var hex = Number(str.charCodeAt(i)).toString(16);
    arr.push(hex);
  }
  return arr.join('');
}

for (const item of $input.all()) {
  let body = item.json;
  if(body){

    // Covering participant id
    if(body.participant){

      if(!body.participant.id || body.participant.id.length == 0)
      {
        delete body.participant;
      } 
      else {
        body.participant.phone = "+" + body.participant.id.split('@').shift();
        body.participant.hex = a2hex(body.participant.id);
      }
    }

    // Covering chat id
    if(body.chat.id){
      if(!body.chat.id.endsWith('@g.us')){
        body.chat.phone = "+" + body.chat.id.split('@').shift();
      } else {
        // adding contact suffix
        if(body.chat.title){
          if(!body.chat.title.endsWith('(GROUP)')) {
            body.chat.title = body.chat.title + ' (GROUP)'
          }
        } else {
          body.chat.title = 'UNKNOWN (GROUP)'
        }
      }
      body.chat.hex = a2hex(body.chat.id);
    }
  }
}
return $input.all();
